import React, { useState } from "react";
import { InputGroup, FormControl, Form } from "react-bootstrap";
import SelectTokenModal from "../Modal/SelectTokenModal";
import "./inputStyle.scss";

const CustomInputgroup = ({ className, icon, title, states }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => {
    setShow(false);
    states.tList.resetTList_chg();
  };
  const handleShow = () => setShow(true);
  return (
    <InputGroup className={`customInp_style ${className}`}>
      <InputGroup.Text>
        <div className="coinSelect_wrap">
          <img src={icon} alt="coin_icon" />
          <div className="coinSelect">
            <span>{title} </span>
            <span className="selectOption" onClick={handleShow}>
              {states.tList.val}
            </span>
          </div>
          <SelectTokenModal show={show} hideCbk={handleClose} state={states.tList} />
        </div>
      </InputGroup.Text>
      <input class="Form" onChange={states.token.cbk} value={states.token.val} inputmode="decimal" autocomplete="off" autocorrect="off" type="text" pattern="^[0-9]*[.,]?[0-9]*$" placeholder="0.0" minlength="1" maxlength="79" spellcheck="false"  ></input>
      {/* <FormControl placeholder="0.00"  onChange={} type='number' onKeyPress={(event) =>(event.code !=101 && event.code!=69)
                        }/> */}
    </InputGroup>
  );
};

export default CustomInputgroup;
